package patches.buildTypes

import jetbrains.buildServer.configs.kotlin.v2019_2.*
import jetbrains.buildServer.configs.kotlin.v2019_2.BuildType
import jetbrains.buildServer.configs.kotlin.v2019_2.ui.*

/*
This patch script was generated by TeamCity on settings change in UI.
To apply the patch, create a buildType with id = 'DomumOrnamentum_Alpha_Release'
in the project with id = 'DomumOrnamentum_Alpha', and delete the patch script.
*/
create(RelativeId("DomumOrnamentum_Alpha"), BuildType({
    templates(RelativeId("BuildWithRelease"))
    id("DomumOrnamentum_Alpha_Release")
    name = "Release"
    description = "Releases the mod as Alpha to CurseForge"

    params {
        param("Project.Type", "mods")
        param("jdk.version", "jdk16")
        param("env.Version.Patch", "%dep.LetSDevTogether_DomumOrnamentum_OfficialPublications_CommonB.build.number%")
    }

    dependencies {
        snapshot(RelativeId("DomumOrnamentum_OfficialPublications_CommonB")) {
            reuseBuilds = ReuseBuilds.NO
            onDependencyFailure = FailureAction.FAIL_TO_START
        }
    }
}))

